import React, {useState} from 'react';
import {Link} from 'react-router-dom';

import { Drawer, Divider, Button} from '@mui/material/';
import { List, ListItem, ListItemIcon, ListItemText } from '@mui/material/';

const styles = {
  sideNav: {
    marginTop: '-60px',
    zIndex: 3,
    marginLeft: '0px',
    position: 'fixed',
  },
  link: {
    color: 'black',
    textDecoration: 'none',
  }
};

const RouteDrawer = ({links}) => {
  const[isOpened, setOpened] = useState(false);

  const toggleDrawerStatus = () => {
    setOpened(true);

  }

  const closeDrawer = () => {
    setOpened(false);

  }

  return(
    <div>
      <div style={styles.sideNav}>
        <Button onClick={toggleDrawerStatus()}>
          Pages
        </Button>
      </div>
      <Divider/>

      <Drawer
        variant="temporary"
        open={isOpened}
        onClose={closeDrawer()}
      >
        {links.map(link => {
          return(
            <Link to={link.path} style={styles.link}>
            <List>
              <ListItem button key={link.name}>
                <ListItemText primary={link.name}/>
              </ListItem>
            </List>
          </Link>
          );
        })}

      </Drawer>

    </div>

  );

};

export default RouteDrawer;
